  `numberart` varchar(50) NOT NULL COMMENT '发布月份',
  `imageFile` varchar(255) NOT NULL COMMENT '栏目图片',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COMMENT='栏目表';

